<style lang="scss">
    @import '../../../sass/uikit/variables-theme';
    @import '../../../sass/variables';
    @import '../../../sass/mixins';
    .tm-quick-order {
        text-align: center;
        margin-top: -($global-large-margin + $global-gutter);
        &__container {
            height: 100vw;
            margin-top: $global-large-margin;
        }
        @include media-mob($s) {
            text-align: left;
            &__container {
                height: 500px;
            }
        }
        @include media-mob($m) {
            margin-top: -$global-xlarge-margin;
            &__container {
                height: 600px;
            }
        }
        @include media-mob($l) {
            margin-top: -($global-xlarge-margin + $global-large-margin);
            clip-path: polygon(100% 0%, 50% 8%, 0% 0%, 0% 100%, 100% 100%);
            &__container {
                margin-top: $global-xlarge-margin;
            }
        }
        @include media-mob($xl) {
            margin-top: -($global-xlarge-margin + $global-large-margin + $global-medium-margin);
            &__container {
                margin-top: ($global-xlarge-margin + $global-medium-margin);
            }
        }
        @include media-mob($hxl) {
            &__container {
                height: 700px;
            }
        }
        &__content {
            max-width: $width-xlarge-width;
        }
        .tm-clock {
            position: absolute;
            width: 100vw;
            height: auto;
            top: 50%;
            left: 0;
            transform: translateY(-50%);
            opacity: .08;
            @include media-mob($s) {
                width: auto;
                height: 500px;
                left: $global-gutter;
            }
            @include media-mob($m) {
                height: 600px;
                left: $global-medium-margin;
            }
            @include media-mob($l) {
                height: 600px;
                left: 50%;
                transform: translate(-70%,-50%);
            }
            @include media-mob($hxl) {
                height: 700px;
            }
            svg {
                width: inherit;
                height: inherit;
            }
        }
    }
</style>
